mapper.select:
select p.id,w.id as warning_id,p.device_id,d.imsi ,p.name as ammeter_number,w.warning_date,w.warning_desc,d.type as ammeter_type,w.warning_type,
w.warning_status,w.reason,w.rule_id,p.device_type,w.eliminate_date,
case when w.warning_status = 0 then '待处理'
else '已消除' end as status_name
from ammeter_warning w,ammeter_device d,ammeter_position p
where w.ammeter_id = d.id and d.imsi = p.imei
and p.status !=3

      and p.device_type = #{deviceType,jdbcType=INTEGER}


  and w.warning_status = #{status,jdbcType=INTEGER}


      and w.rule_id = #{ruleId,jdbcType=INTEGER}


  and p.id = #{positionId,jdbcType=INTEGER}


  and p.imei like CONCAT('%',#{imei,jdbcType=VARCHAR},'%')


  and p.name like CONCAT('%',#{name,jdbcType=VARCHAR},'%')


      and p.channel_id =  #{channelId,jdbcType=INTEGER}


      and   d.type  like CONCAT('%',#{ammeterType,jdbcType=VARCHAR},'%')


       AND w.warning_date >= #{dateTime} 


  and p.device_id=#{deviceId,jdbcType=VARCHAR}

  order by w.warning_date desc

mapper.insert:

  SELECT LAST_INSERT_ID()

insert into ammeter_warning (ammeter_id, warning_status, warning_date, 
  create_date, create_by, warning_desc,warning_type,reason,messageFlag,rule_id
  )
values (#{ammeterId,jdbcType=INTEGER}, #{warningStatus,jdbcType=CHAR}, #{warningDate,jdbcType=TIMESTAMP}, 
  #{createDate,jdbcType=TIMESTAMP}, #{createBy,jdbcType=VARCHAR}, #{warningDesc,jdbcType=VARCHAR},#{warningType,jdbcType=INTEGER}, #{reason,jdbcType=VARCHAR}
,#{messageFlag,jdbcType=INTEGER},#{ruleId,jdbcType=INTEGER}
  )

mapper.update:
UPDATE ammeter_warning
set warning_status = #{warningStatus,jdbcType=CHAR}
where ammeter_id = #{ammeterId,jdbcType=INTEGER} and warning_type = #{warningType,jdbcType=VARCHAR}

mapper.select:
select p.id,d.id as ammeter_id, p.device_type, a.warning_desc as battery_warning, b.warning_desc as sensor_warning, c.warning_desc as surfaceDistance_warning, d.warning_desc as tiltSensor_warning, e.warning_desc as waterLevelSensor_warning, f.warning_desc as temperature_warning, g.warning_desc as humidity_warning, h.warning_desc as smoke_warning from ammeter_wellcover wc INNER JOIN ammeter_position p on wc.position_id = p.id INNER JOIN ammeter_device d on p.imei = d.imsi LEFT JOIN (select w1.ammeter_id,w1.warning_desc from ammeter_warning w1 INNER JOIN ammeter_rule r1 on w1.rule_id = r1.id and r1.rule_params='batteryStatus' and w1.warning_status = 0 ) a on a.ammeter_id = d.id LEFT JOIN (select w2.ammeter_id,w2.warning_desc from ammeter_warning w2 INNER JOIN ammeter_rule r2 on w2.rule_id = r2.id and r2.rule_params='sensor' and w2.warning_status = 0 ) b on b.ammeter_id = d.id LEFT JOIN (select w3.ammeter_id,w3.warning_desc from ammeter_warning w3 INNER JOIN ammeter_rule r3 on w3.rule_id = r3.id and r3.rule_params='surfaceDistance' and w3.warning_status = 0 ) c on c.ammeter_id = d.id LEFT JOIN (select w4.ammeter_id,w4.warning_desc from ammeter_warning w4 INNER JOIN ammeter_rule r4 on w4.rule_id = r4.id and r4.rule_params='tiltSensor' and w4.warning_status = 0 ) d on d.ammeter_id = d.id LEFT JOIN (select w5.ammeter_id,w5.warning_desc from ammeter_warning w5 INNER JOIN ammeter_rule r5 on w5.rule_id = r5.id and r5.rule_params='waterLevelSensor' and w5.warning_status = 0 ) e on e.ammeter_id = d.id LEFT JOIN (select w6.ammeter_id,w6.warning_desc from ammeter_warning w6 INNER JOIN ammeter_rule r6 on w6.rule_id = r6.id and r6.rule_params='enTemperature' and w6.warning_status = 0 ) f on f.ammeter_id = d.id LEFT JOIN (select w7.ammeter_id,w7.warning_desc from ammeter_warning w7 INNER JOIN ammeter_rule r7 on w7.rule_id = r7.id and r7.rule_params='enHumidity' and w7.warning_status = 0 ) g on g.ammeter_id = d.id LEFT JOIN (select w8.ammeter_id,w8.warning_desc from ammeter_warning w8 INNER JOIN ammeter_rule r8 on w8.rule_id = r8.id and r8.rule_params='smokeWarning' and w8.warning_status = 0 ) h on h.ammeter_id = d.id where 1=1
and p.status !=3
and p.id =#{positionId,jdbcType=INTEGER}
